`function dfs(graph: number[][], start: number): number[] {
  const visited = new Set<number>();
  const result: number[] = [];

  function dfsHelper(vertex: number) {
    visited.add(vertex);
    result.push(vertex);

    for (const neighbor of graph[vertex]) {
      if (!visited.has(neighbor)) {
        dfsHelper(neighbor);
      }
    }
  }

  dfsHelper(start);
  return result;
}

// Iterative version using explicit stack
function dfsIterative(graph: number[][], start: number): number[] {
  const visited = new Set<number>();
  const stack = [start];
  const result: number[] = [];

  while (stack.length > 0) {
    const vertex = stack.pop()!;

    if (!visited.has(vertex)) {
      visited.add(vertex);
      result.push(vertex);

      // Add neighbors to stack (in reverse order for same traversal as recursive)
      for (let i = graph[vertex].length - 1; i >= 0; i--) {
        const neighbor = graph[vertex][i];
        if (!visited.has(neighbor)) {
          stack.push(neighbor);
        }
      }
    }
  }

  return result;
}

`function bfs(graph: number[][], start: number): number[] {
  const visited = new Set<number>();
  const queue: number[] = [start];
  const result: number[] = [];

  visited.add(start);

  while (queue.length > 0) {
    const vertex = queue.shift()!;
    result.push(vertex);

    for (const neighbor of graph[vertex]) {
      if (!visited.has(neighbor)) {
        visited.add(neighbor);
        queue.push(neighbor);
      }
    }
  }

  return result;
}

// BFS for shortest path
function bfsShortestPath(graph: number[][], start: number, target: number): number[] {
  const visited = new Set<number>();
  const queue: Array<{vertex: number, path: number[]}> = [{vertex: start, path: [start]}];

  visited.add(start);

  while (queue.length > 0) {
    const {vertex, path} = queue.shift()!;

    if (vertex === target) {
      return path;
    }

    for (const neighbor of graph[vertex]) {
      if (!visited.has(neighbor)) {
        visited.add(neighbor);
        queue.push({vertex: neighbor, path: [...path, neighbor]});
      }
    }
  }

  return []; // No path found
}`

`interface Edge {
  to: number;
  weight: number;
}

function dijkstra(graph: Edge[][], start: number): number[] {
  const distances = new Array(graph.length).fill(Infinity);
  const pq = [{vertex: start, distance: 0}];
  distances[start] = 0;

  while (pq.length > 0) {
    // Sort to get minimum distance (use proper heap in production)
    pq.sort((a, b) => a.distance - b.distance);
    const {vertex, distance} = pq.shift()!;

    // Skip if we've found a better path already
    if (distance > distances[vertex]) continue;

    for (const edge of graph[vertex]) {
      const newDist = distances[vertex] + edge.weight;
      if (newDist < distances[edge.to]) {
        distances[edge.to] = newDist;
        pq.push({vertex: edge.to, distance: newDist});
      }
    }
  }

  return distances;
}

// With path reconstruction
function dijkstraWithPath(graph: Edge[][], start: number, target: number): {distance: number, path: number[]} {
  const distances = new Array(graph.length).fill(Infinity);
  const previous = new Array(graph.length).fill(-1);
  const pq = [{vertex: start, distance: 0}];

  distances[start] = 0;

  while (pq.length > 0) {
    pq.sort((a, b) => a.distance - b.distance);
    const {vertex, distance} = pq.shift()!;

    if (vertex === target) break;
    if (distance > distances[vertex]) continue;

    for (const edge of graph[vertex]) {
      const newDist = distances[vertex] + edge.weight;
      if (newDist < distances[edge.to]) {
        distances[edge.to] = newDist;
        previous[edge.to] = vertex;
        pq.push({vertex: edge.to, distance: newDist});
      }
    }
  }

  // Reconstruct path
  const path: number[] = [];
  let current = target;
  while (current !== -1) {
    path.unshift(current);
    current = previous[current];
  }

  return {
    distance: distances[target],
    path: distances[target] === Infinity ? [] : path
  };
}`

`class UnionFind {
  private parent: number[];
  private rank: number[];
  private components: number;

  constructor(n: number) {
    this.parent = Array.from({length: n}, (_, i) => i);
    this.rank = new Array(n).fill(0);
    this.components = n;
  }

  find(x: number): number {
    if (this.parent[x] !== x) {
      // Path compression: make parent point directly to root
      this.parent[x] = this.find(this.parent[x]);
    }
    return this.parent[x];
  }

  union(x: number, y: number): boolean {
    const rootX = this.find(x);
    const rootY = this.find(y);

    if (rootX === rootY) {
      return false; // Already connected
    }

    // Union by rank: attach smaller tree under root of larger tree
    if (this.rank[rootX] < this.rank[rootY]) {
      this.parent[rootX] = rootY;
    } else if (this.rank[rootX] > this.rank[rootY]) {
      this.parent[rootY] = rootX;
    } else {
      this.parent[rootY] = rootX;
      this.rank[rootX]++;
    }

    this.components--;
    return true;
  }

  connected(x: number, y: number): boolean {
    return this.find(x) === this.find(y);
  }

  getComponents(): number {
    return this.components;
  }
}`

`// DFS-based topological sort
function topologicalSortDFS(graph: number[][]): number[] {
  const visited = new Set<number>();
  const result: number[] = [];

  function dfs(vertex: number) {
    visited.add(vertex);

    for (const neighbor of graph[vertex]) {
      if (!visited.has(neighbor)) {
        dfs(neighbor);
      }
    }

    result.unshift(vertex); // Add to front when done
  }

  for (let i = 0; i < graph.length; i++) {
    if (!visited.has(i)) {
      dfs(i);
    }
  }

  return result;
}

// Kahn's algorithm
function topologicalSortKahn(graph: number[][]): number[] {
  const inDegree = new Array(graph.length).fill(0);
  const result: number[] = [];
  const queue: number[] = [];

  // Calculate in-degrees
  for (let i = 0; i < graph.length; i++) {
    for (const neighbor of graph[i]) {
      inDegree[neighbor]++;
    }
  }

  // Add vertices with in-degree 0 to queue
  for (let i = 0; i < inDegree.length; i++) {
    if (inDegree[i] === 0) {
      queue.push(i);
    }
  }

  while (queue.length > 0) {
    const vertex = queue.shift()!;
    result.push(vertex);

    for (const neighbor of graph[vertex]) {
      inDegree[neighbor]--;
      if (inDegree[neighbor] === 0) {
        queue.push(neighbor);
      }
    }
  }

  // Check for cycles
  return result.length === graph.length ? result : [];
}`

`function isBipartite(graph: number[][]): boolean {
  const color = new Array(graph.length).fill(-1);

  for (let start = 0; start < graph.length; start++) {
    if (color[start] === -1) {
      // BFS for each component
      const queue = [start];
      color[start] = 0;

      while (queue.length > 0) {
        const vertex = queue.shift()!;

        for (const neighbor of graph[vertex]) {
          if (color[neighbor] === -1) {
            // Color with opposite color
            color[neighbor] = 1 - color[vertex];
            queue.push(neighbor);
          } else if (color[neighbor] === color[vertex]) {
            // Same color - not bipartite
            return false;
          }
        }
      }
    }
  }

  return true;
}

// DFS version
function isBipartiteDFS(graph: number[][]): boolean {
  const color = new Array(graph.length).fill(-1);

  function dfs(vertex: number, c: number): boolean {
    color[vertex] = c;

    for (const neighbor of graph[vertex]) {
      if (color[neighbor] === -1) {
        if (!dfs(neighbor, 1 - c)) {
          return false;
        }
      } else if (color[neighbor] === color[vertex]) {
        return false;
      }
    }

    return true;
  }

  for (let i = 0; i < graph.length; i++) {
    if (color[i] === -1) {
      if (!dfs(i, 0)) {
        return false;
      }
    }
  }

  return true;
}`
